import React, { useState } from "react";
import "./Style.css"


const App = ()=>{


  const [calc , setCalc ] = useState("");
  const [result , setResult ] = useState("");

  const ops = ['/' , '*' , '+', '-', '.'];

  const updateCal = value =>{
    setCalc(calc + value);
  }

    const createDigit = ()=>{
      const digit = [];
      for(let i=1;i<10;i++){
        digit.push(
          <button 
            onClick={()=> createDigit(i.toString())} 
            key={i}>
              {i}
          </button>
        )
      }
      return digit;
    }

  return(
    <div className="App">
      <div className="claculator">
        <div className="display">
          {result ? <span>(0)</span> : ''} {cal || "0"}

        </div>
        <div className="operator">
          <button onClick={()=> createDigit('+')} >+</button>
          <button onClick={()=> createDigit('-')} >-</button>
          <button onClick={()=> createDigit('/')}>/</button>
          <button onClick={()=> createDigit('*')}>*</button>
          <button >DEL</button>
        </div>

        <div className="digit">
          { createDigit()}

          <button onClick={()=> createDigit('0')}>0</button>
          <button onClick={()=> createDigit('.')}>.</button>
          <button>=</button>
        </div>

      </div>
    </div>
  )

}



export default App;
