import React, { useState } from "react";
import List from "./component/List";
import { myTodos } from "./data/todos";
import "./Style.css";
import uuid from "react-uuid";



const App = ()=>{
  const [todos , settodos] = useState(myTodos)
  const [value , setValue] = useState(' ')
  const handleChange = (e)=>{
    setValue(e.target.value);
    console.log(value)

  }

  // SUBMIT
  const handleSubmit = (e)=>{
    e.preventDefault();

    if(!value || value.length <= 3){
      alert("TODO must be atlest 3 character")
    }


    const newTodo = [...todos , {
      id: uuid(),
      name: value,
      completed :false
    }]
      settodos(newTodo);
      setValue(" ")
  }

  // remove 
  const handleRemove = (id)=>{
    console.log("clicked on remove")
    const filtering = todos.filter((todos)=>{
      return todos.id == id 


    })
    if(todos.id !== id )
    {
      console.log("yes")
    }
    else{
      console.log("no")
    }


    settodos(filtering);

  }

  return(
    <>
      <div className="App">
        <form className="form" action="" onSubmit={handleSubmit}>
          <h1>Today's Task</h1>
          <div className="input-container"> 
          <input type="text" value={value} onChange = {handleChange} placeholder="Add a task" />
          <div className="btn-submit">
            <button>+ Add ToDO</button>
          </div>
          </div>
        </form>

        <ul className="todo-container">
          {
            todos.map((todo)=>{
              const {id , name , completed} = todo
              return (
                <List 
                  key = {id}
                  name = {name}
                  completed = {completed}
                  removeTodo = {handleRemove}
                  />
              )

            })

            }

        </ul>
      </div>
    </>
  )

}

export default App;
