import React, { useReducer } from "react";
import DigitButton from "./DigitButton";

import "./Style.css";


const ACTION = {
  ADD_DIGIT:'Add-digit',
  CHOOSE_OPERATION :'choose-operation',
  CLEAR : 'clear',
  DELETE_DIGIT : 'delete-digit',
  EVALUATE : 'evaluate'

}


function reducer(state , {type , paylode}){
  if(type == ACTION.ADD_DIGIT){
    return {
      ...state , 
      currentoperand : `${currentoperand}${paylode.digit}`
    }
  }
}



const App = ()=>{

  const [{currentoperand , previousoperand , operation}]= useReducer(reducer , {})







    return(
      <>
     <div className="claculator-grid">
      <div className="output">
        <div className="previous-output">{previousoperand} {operation}</div>
        <div className="current-output">{currentoperand} </div>
      </div>
      <button className="span-two">AC</button>
      <button>DEL</button>
      <DigitButton digit="รท" dispatch={dispatch} />
      <button>1</button>
      <button>2</button>
      <DigitButton digit="3" dispatch={dispatch}  />
      <button>X</button>
      <button>4</button>
      <button>5</button>
      <button>6</button>
      <button>+</button>
      <button>7</button>
      <button>8</button>
      <button>9</button>
      <button>-</button>
      <button>.</button>
      <button>0</button>
      <button className="span-two">=</button>
     </div>
      </>
    )

}


export default App;
