import React, { useReducer } from "react";
import DigitButton from "./DigitButton";

import "./Style.css";


export const ACTION = {
  ADD_DIGIT: "add-digit",
  CHOOSE_OPERATION: "choose-operation",
  CLEAR: "clear",
  DELETE_Digit : "delete-digit",
  EVALUATE : "evaluate"
}


const App = ()=>{

  function reducer(state , {type , payload}){

    switch(type){
      case ACTION.ADD_DIGIT:
        return{
          ...state,
          currentOperande : `${currentOperande || ""} ${payload.digit}`
        }
    }

  }





  const [{currentOperande , previousOperande , operation}, dispatch] = useReducer(reducer , {})

    // dispatch( {type : ACTION.ADD_DIGIT , payload :{digit : 1} })
    return(
      <>
     <div className="claculator-grid">
      <div className="output">
        <div className="previous-output">{previousOperande} {operation}</div>
        <div className="current-output">{currentOperande}</div>
      </div>
      <button className="span-two">AC</button>
      <button>DEL</button>
      <DigitButton digit="รท" dispatch={dispatch} />
      <button>1</button>
      <button>2</button>
      <button>3</button>
      <button>X</button>
      <button>4</button>
      <button>5</button>
      <button>6</button>
      <button>+</button>
      <button>7</button>
      <button>8</button>
      <button>9</button>
      <button>-</button>
      <button>.</button>
      <button>0</button>
      <button className="span-two">=</button>
     </div>
      </>
    )

}


export default App;
